/**
 * Options object for `Bugfender.getUserFeedback` with customised modal strings.
 */
export interface UserFeedbackOptions {
    /** Default: `Feedback`. */
    title?: string;
    /** Default: `Please insert your feedback here and click send`. */
    hint?: string;
    /** Default: `Subject…`. */
    subjectPlaceholder?: string;
    /** Default: `Your feedback…`. */
    feedbackPlaceholder?: string;
    /** Default: `Send`. */
    submitLabel?: string;
    /** Default: `Close`. **iOS only**. */
    closeLabel?: string;
}
export interface UserFeedbackResultSuccess {
    /** The user has sent the feedback. */
    isSent: true;
    /** Bugfender URL for the feedback. */
    feedbackURL: string;
}
export interface UserFeedbackResultCancel {
    /** The user has closed the modal without sending it. */
    isSent: false;
}
export type UserFeedbackResult = UserFeedbackResultSuccess | UserFeedbackResultCancel;
